import React, { useState } from "react";
import * as S from "styled-components";

const MovieDetailPage = () => {
    const [expandedReview, setExpandedReview] = useState(null);

    const ReviewClick = (index) => {
        if (expandedReview === index) {
            setExpandedReview(null);
        } else {
            setExpandedReview(index);
        }
    };

    return (
        <S.MovieDetailContainer>
            <S.PosterContainer>
                <S.Poster src="movie-poster.jpg" alt="Movie Poster" />
            </S.PosterContainer>
            <S.MovieDetails>
                <S.Title>Movie Title</S.Title>
                <S.GenreList>
                    <S.Genre>Genre 1</S.Genre>
                    <S.Genre>Genre 2</S.Genre>
                </S.GenreList>
                <S.Synopsis>
                    Movie synopsis goes here. Lorem ipsum dolor sit amet,
                    consectetur adipiscing elit. Sed auctor, magna a bibendum
                    bibendum, augue magna tincidunt enim, eget ultricies magna
                    augue eget lorem.
                </S.Synopsis>
                <S.Credits>
                    <S.CreditItem>Actor 1</S.CreditItem>
                    <S.CreditItem>Actor 2</S.CreditItem>
                    <S.CreditItem>Actor 3</S.CreditItem>
                </S.Credits>
                <S.ReleaseDate>Release Date: YYYY-MM-DD</S.ReleaseDate>
                <S.ReviewContainer>
                    <S.ReviewTitle>Reviews</S.ReviewTitle>
                    <S.ReviewList>
                        {reviews.map((review, index) => (
                            <S.ReviewItem
                                key={index}
                                onClick={() => ReviewClick(index)}
                            >
                                <S.ReviewText
                                    expanded={expandedReview === index}
                                >
                                    {review}
                                </S.ReviewText>
                            </S.ReviewItem>
                        ))}
                    </S.ReviewList>
                </S.ReviewContainer>
            </S.MovieDetails>
        </S.MovieDetailContainer>
    );
};
