import React, { useState } from "react";
import * as S from "styled-components";

const MovieDetailPage = () => {
    const [expandedReview, setExpandedReview] = useState(null);

    const ReviewClick = (index) => {
        if (expandedReview === i) {
            setExpandedReview(null);
        } else {
            setExpandedReview(i);
        }
    };

    return (
        <S.MovieDetailContainer>
            <S.PosterContainer>
                <S.Poster
                    src="https://an2-img.amz.wtchn.net/image/v2/xaNLiC73hTobTa8cnnccJw.jpg?jwt=ZXlKaGJHY2lPaUpJVXpJMU5pSjkuZXlKdmNIUnpJanBiSW1SZk5Ea3dlRGN3TUhFNE1DSmRMQ0p3SWpvaUwzWXlMM04wYjNKbEwybHRZV2RsTHpVM09EZzROamsyTmpFM09ESTJNU0o5LkJDMDhNcU5DWWlqTFRnalIybTJkREdBbzAzT1B4UHFXcWtZS3ZyTi1TMTg"
                    alt="Movie Poster"
                />
            </S.PosterContainer>
            <S.MovieDetails>
                <S.Title>Movie Title</S.Title>
                <S.GenreList>
                    <S.Genre>Genre 1</S.Genre>
                    <S.Genre>Genre 2</S.Genre>
                </S.GenreList>
                <S.Synopsis>
                    Movie synopsis goes here. Lorem ipsum dolor sit amet,
                    consectetur adipiscing elit. Sed auctor, magna a bibendum
                    bibendum, augue magna tincidunt enim, eget ultricies magna
                    augue eget lorem.
                </S.Synopsis>
                <S.Credits>
                    <S.CreditItem>Actor 1</S.CreditItem>
                    <S.CreditItem>Actor 2</S.CreditItem>
                    <S.CreditItem>Actor 3</S.CreditItem>
                </S.Credits>
                <S.ReleaseDate>Release Date: YYYY-MM-DD</S.ReleaseDate>
                <S.ReviewContainer>
                    <S.ReviewTitle>Reviews</S.ReviewTitle>
                    <S.ReviewList>
                        {reviews.map((review, i) => (
                            <S.ReviewItem
                                key={i}
                                onClick={() => ReviewClick(i)}
                            >
                                <S.ReviewText expanded={expandedReview === i}>
                                    {review}
                                </S.ReviewText>
                            </S.ReviewItem>
                        ))}
                    </S.ReviewList>
                </S.ReviewContainer>
            </S.MovieDetails>
        </S.MovieDetailContainer>
    );
};
