import styled from "styled-components";

export const CarouselContainer = styled.div`
    .movie-slider {
        transition: transform 0.5s;
    }
    .carousel-container {
        overflow: hidden;
        &:hover {
            overflow: visible;
        }
    }
`;
export const CustomArrowPrev = styled.button`
    position: absolute;
    top: 50%;
    left: 20px;
    transform: translateY(-50%);
    z-index: 10; /* 높은 z-index 값 설정 */
    /* 기타 스타일 */
`;

export const CustomArrowNext = styled(CustomArrowPrev)`
    left: auto;
    right: 20px;
`;
