// PopularMovieSlide.jsx
import React, { useRef } from "react";
import { usePopularMoviesQuery } from "../../../../hooks/usePopularMovies";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import MovieCard from "../MovieCard/MovieCard";
import * as S from "./popularMovieSlide.styled";

const responsive = {
    // ... (기존 responsive 객체)
};

const PopularMovieSlide = () => {
    const { data, isLoading, isError, error } = usePopularMoviesQuery();
    const carouselRef = useRef(null);

    const handleBeforeSlideChange = (
        currentSlideIndex,
        currentSlideObject,
        newSlideObject
    ) => {
        const movieCards = carouselRef.current.innerRef.current.children;

        Array.from(movieCards).forEach((card, index) => {
            const scale = index === newSlideObject.currentSlide ? 1.5 : 1;
            card.style.transform = `scale(${scale})`;
        });
    };

    if (isLoading) {
        return <div>Loading... loadingspinner 추가해야됨</div>;
    }

    if (isError) {
        return <div>{error.message}</div>;
    }

    return (
        <S.CarouselContainer>
            <h3>Popular Movies</h3>
            <Carousel
                infinite={true}
                centerMode={true}
                containerClass="carousel-container"
                itemClass="movie-slider p-1"
                responsive={responsive}
                beforeSlideChange={handleBeforeSlideChange}
                ref={carouselRef}
            >
                {data.results.map((movie, i) => (
                    <MovieCard movie={movie} key={`movie-${i}`} />
                ))}
            </Carousel>
        </S.CarouselContainer>
    );
};

export default PopularMovieSlide;
