// Moviespage.styled.js
import styled from "styled-components";

export const Container = styled.div`
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
`;

export const Row = styled.div`
    display: flex;
    flex-wrap: wrap;
    margin: -10px;
`;

export const Col = styled.div`
    flex: 0 0 25%;
    max-width: 25%;
    padding: 10px;

    @media (max-width: 992px) {
        flex: 0 0 33.333333%;
        max-width: 33.333333%;
    }

    @media (max-width: 768px) {
        flex: 0 0 50%;
        max-width: 50%;
    }

    @media (max-width: 576px) {
        flex: 0 0 100%;
        max-width: 100%;
    }
`;
