import styled from "styled-components";

export const FooterContainer = styled.footer`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #333333;
    padding: 2rem 2rem;

    @media (max-width: 768px) {
        padding: 1rem;
    }
`;

export const FooterText = styled.p`
    font-size: 14px;
    text-align: center;

    @media (max-width: 768px) {
        font-size: 12px;
    }
`;
